mapper.insert:
update block_attachment
set id =#{id}

    ,name = #{name}


    ,image_url = #{imageUrl}


    ,type = #{type}


    ,detail = #{detail}


    , block_id = #{blockId}


    , parent_id = #{parentId}


    , operator_id = #{operatorId}

,update_time = now()
where id=#{id}

mapper.select:

select * from block_attachment
where delete_flg=0

    and name like concat('%',#{name},'%')


    and block_id=#{blockId}


    and parent_id=#{parentId}


    and type=#{type}

order by id desc

select count(*) from block_attachment
where delete_flg=0

    and name like concat('%',#{name},'%')


    and block_id=#{blockId}


    and parent_id=#{parentId}


    and type=#{type}

select id, name from block_attachment
where delete_flg=0

    and name like concat('%',#{name},'%')


    and block_id=#{blockId}


    and parent_id=#{parentId}


    and type=#{type}

order by id

select * from block_attachment
where delete_flg=0

    and name like concat('%',#{name},'%')


    and block_id=#{blockId}


    and parent_id=#{parentId}


    and type=#{type}

order by id desc
