mapper.insert:
update block
set id =#{id}

    ,name = #{name}


    ,detail = #{detail}


    , enterprise_id = #{enterpriseId}


    , status = #{status}


    , operator_id = #{operatorId}

,update_time = now()
where id=#{id}

mapper.select:

select * from block
where delete_flg=0

    and name like concat('%',#{query.name},'%')


    and plateform_flg=#{query.plateformFlg}


    and status=#{query.status}


    and enterprise_id=#{query.enterpriseId}

order by id desc

select count(*) from block
where delete_flg=0

    and name like concat('%',#{query.name},'%')


    and plateform_flg=#{query.plateformFlg}


    and status=#{query.status}


    and enterprise_id=#{query.enterpriseId}

select id, name from block
where delete_flg=0

    and name like concat('%',#{query.name},'%')


    and plateform_flg=#{query.plateformFlg}


    and enterprise_id=#{query.enterpriseId}

order by id
