mapper.select:
select m.id as moduleId, m.block_id as blockId, m.name as moduleName, c.id as classId, c.name as className, c.module_id, b.id as blockId, b.enterprise_id as enterprise_id, b.name as blockName, b.status as blockStatus, b.detail as blockDetail
from block b left join block_module m on b.id=m.block_id left join block_class c on m.id=c.module_id
where m.delete_flg=0

    and b.enterprise_id=#{enterpriseId}


    and m.name=#{moduleName}

mapper.insert:
update block
set id =#{id}

    ,name = #{name}


    ,detail = #{detail}


    , enterprise_id = #{enterpriseId}


    , status = #{status}


    , operator_id = #{operatorId}

,update_time = now()
where id=#{id}

mapper.select:

select * from block
where delete_flg=0

    and name like concat('%',#{name},'%')


    and status=#{status}


    and enterprise_id=#{enterpriseId}

order by id desc

select count(*) from block
where delete_flg=0

    and name like concat('%',#{name},'%')


    and status=#{status}


    and enterprise_id=#{enterpriseId}

select * from block
where delete_flg=0

    and name like concat('%',#{name},'%')


    and status=#{status}


    and enterprise_id=#{enterpriseId}


    and enterprise_id in (

        #{eid}

    )

order by id desc
