mapper.update:
update block_tip
set id=#{id}

    , detail = #{detail}

where id=#{id}

mapper.select:

select a.id, a.block_id, a.spot_id, a.detail,
       b.title, b.block_id as spot_block_id, b.ath, b.atv, b.fov, b.scode, b.acode, b.type, b.detail as spot_detail, b.seq
from block_tip a join block_tour_spot b on a.spot_id=b.id
where  b.delete_flg=0

    and a.block_id=#{blockId}


    and a.spot_id=#{spotId}


    and b.`type`=#{type}


    and a.block_id in (

        #{bid}

    )

order by a.block_id desc, b.seq

select count(*)
from block_tip a join block_tour_spot b on a.spot_id=b.id
where  b.delete_flg=0

    and a.block_id=#{blockId}


    and a.spot_id=#{spotId}


    and b.`type`=#{type}


    and a.block_id in (

        #{bid}

    )

select a.id,  b.title,
from block_tip a join block_tour_spot b on a.spot_id=b.id
where  b.delete_flg=0

    and block_id=#{blockId}


    and spot_id=#{spotId}

order by id desc

select a.id, a.block_id, a.spot_id, a.detail,
b.title, b.block_id as spot_block_id, b.ath, b.atv, b.fov, b.scode, b.acode, b.type, b.detail as spot_detail, b.seq
from block_tip a join block_tour_spot b on a.spot_id=b.id
where  b.delete_flg=0

    and a.block_id=#{blockId}


    and a.spot_id=#{spotId}


    and b.`type`=#{type}


    and a.block_id in (

        #{bid}

    )

order by a.block_id desc, b.seq
