mapper.insert:
update comment
set update_time = now()

    , content = #{content}


    , content_type = #{contentType}


    , related_id = #{relatedId}


    , `type` = #{type}


    , ext_type = #{extType}


    , ext_related_id = #{extRelatedId}

where id=#{id}

mapper.select:

select c.*, p.sgname as userName, p.avatar as userAvatar from comment c left join user_profile p on c.user_id = p.user_id
where c.delete_flg=0

    and c.type = #{type}


    and c.related_id = #{relatedId}


    and c.ext_type = #{extType}


    and c.ext_related_id = #{extRelatedId}

order by c.id desc

select count(*) from comment c
where c.delete_flg=0

    and c.type = #{type}


    and c.related_id = #{relatedId}


    and c.ext_type = #{extType}


    and c.ext_related_id = #{extRelatedId}

select c.*, p.sgname as userName, p.avatar as userAvatar from comment c left join user_profile p on c.user_id = p.user_id
where c.delete_flg=0

    and c.type = #{type}


    and c.related_id = #{relatedId}


    and c.ext_type = #{extType}


    and c.ext_related_id = #{extRelatedId}

order by c.id desc
