mapper.update:
update department
set name = #{name}

    , enterprise_id = #{enterpriseId}


    , manager_id = #{managerId}


    , parent_id = #{parentId}


    , operator_id = #{operatorId}

,update_time = now()
where id=#{departmentId}

mapper.select:
select count(*) from department d where d.delete_flg=0
and d.name like concat('%',#{name},'%')
and d.enterprise_id=#{enterpriseId}
