mapper.update:
update enterprise
set update_time = now()

    , name = #{name}


    , detail = #{detail}


    , image_path = #{imagePath}


    , base_path = #{basePath}

where id=#{id}

mapper.select:
select count(*) from enterprise d

    left join enterprise_user eu on d.id=eu.enterprise_id

where 1=1

    and d.name like concat('%',#{name},'%')


    and d.plateform_flg = #{plateformFlg}


    and eu.manager_flg=1 and eu.user_id=#{managerId}

select e.*, u.manager_flg from enterprise e, enterprise_user u where e.id=u.enterprise_id and e.plateform_flg=0
and u.user_id=#{userId}
and e.name like concat('%',#{name},'%')
and e.plateform_flg = #{plateformFlg}
order by id desc
