mapper.update:
update enterprise_user
set update_time = now()

    , manager_flg = #{managerFlg}

where enterprise_id=#{enterpriseId} and user_id=#{userId}

mapper.select:

select eu.id, eu.enterprise_id as enterpriseId, eu.manager_flg as managerFlg, eu.user_id as userId,eu.create_time as createTime, eu.update_time as updateTime, p.sgname as userSgname, d.id as departmentId, d.name as departmentName, d.parent_id as parentId, d.manager_id as managerId
from enterprise_user eu left join user_profile p on eu.user_id=p.user_id
    left join department_user du on du.user_id=eu.user_id
    left join department d on du.department_id=d.id
where 1=1

    and eu.enterprise_id=#{enterpriseId}


    and eu.user_id=#{userId}


    and p.sgname like concat('%',#{sgname},'%')


    and d.id=#{departmentId}

order by d.id desc

select eu.id, eu.enterprise_id as enterpriseId, eu.manager_flg as managerFlg, eu.user_id as userId,eu.create_time as createTime, eu.update_time as updateTime, e.name, e.detail, e.avatar, e.image_path, e.base_path, p.sgname as userSgname, d.id as departmentId, d.name as departmentName, d.parent_id as parentId, d.manager_id as managerId from enterprise_user eu left join user_profile p on eu.user_id=p.user_id left join enterprise e on eu.enterprise_id=e.id LEFT JOIN department_user du on du.user_id=eu.user_id left join department d on d.enterprise_id=eu.enterprise_id and d.id=du.department_id where 1=1
and eu.enterprise_id=#{enterpriseId}
and eu.user_id=#{userId}
and p.sgname like concat('%',#{sgname},'%')
and d.id=#{departmentId}
order by d.id desc

select count(*)
from enterprise_user eu left join user_profile p on eu.user_id=p.user_id left join department d on d.enterprise_id=eu.enterprise_id
where 1=1

    and eu.enterprise_id=#{enterpriseId}



    and p.sgname like concat('%',#{sgname},'%')


    and d.id=#{departmentId}

select eu.*, p.sgname as userSgname, d.id as departmentId, d.name as departmentName
from enterprise_user eu left join user_profile p on eu.user_id=p.user_id left join department d on d.enterprise_id=eu.enterprise_id
where eu.id=#{id}
