mapper.update:
update news set update_time=now()

    ,status=#{status}


    , publish_time = #{publishTime}


    , top_flg = #{topFlg}


    , title = #{title}


, institution=#{institution}


, summary = #{summary}


,author = #{author}


, content_url = #{contentUrl}


, image_url = #{imageUrl}


, content_html=#{contentHtml}

where id=#{id}

mapper.select:
select count(*) from news where 1=1

    and status=#{status}


    and status in(#{item})
