mapper.select:
select * from role_permission rp left join user_role ur on rp.role_id=ur.role_id
where 1=1

    and rp.class_id=#{classId}


    and ur.user_id = #{userId}

select distinct(ur.user_id) as userId, u.mobile, up.sgname, up.avatar, up.email,up.description, d.name as departmentName from user_role ur left join role_permission rp on ur.role_id=rp.role_id left join user_profile up on ur.user_id=up.user_id left join user u on u.id=ur.user_id left join department_user du on ur.user_id=du.user_id left join department d on du.department_id = d.id
left join project p on p.class_id=rp.class_id
where rp.operation = #{operation}
and p.id=#{projectId}
and rp.class_id=#{classId}
order by up.sgname desc

select distinct(ur.user_id) as userId, u.mobile, up.sgname, up.avatar, up.email,up.description, d.name as departmentName from user_role ur left join role_permission rp on ur.role_id=rp.role_id left join user_profile up on ur.user_id=up.user_id left join user u on u.id=ur.user_id left join department_user du on ur.user_id=du.user_id left join department d on du.department_id = d.id
left join project p on p.class_id=rp.class_id
where rp.operation = #{operation}
and p.id=#{projectId}
and rp.class_id=#{classId}
order by up.sgname desc

select count(1)
from user_role ur
left join role_permission rp on ur.role_id=rp.role_id
left join department_user du on ur.user_id=du.user_id

  left join project p on p.class_id=rp.class_id

where rp.operation = #{operation}

    and p.id=#{projectId}


    and rp.class_id=#{classId}
