mapper.update:
update scene set id=#{id}

    ,name = #{name}


    ,detail = #{detail}


    ,src_block_id = #{srcBlockId}


    ,pano_image_url = #{panoImageUrl}


    ,image_id = #{imageId}


    ,public_flg = #{publicFlg}


    ,location_y = #{locationY}


    ,location_x = #{locationX}


    ,scene_type_id = #{sceneTypeId}


    ,slice_status = #{sliceStatus}


    ,seq = #{seq}

where id=#{id}

mapper.select:

select s.*,si.path as imageUrl, bst.name as sceneTypeName, bst.rel_scene_type_id as relSceneTypeId, rst.name as relSceneTypeName

, ps.seq

from scene s left join block_image si on s.image_id=si.id
left join block_scene_type bst on bst.id=s.scene_type_id
left join block_scene_type rst on bst.rel_scene_type_id=rst.id

left join project_scene ps on s.id=ps.scene_id

where s.delete_flg=0

    and ps.project_id = #{projectId}


    and s.block_id = #{blockId}


    and s.src_block_id = #{srcBlockId}


    and s.public_flg = #{publicFlg}


    and s.scene_type_id = #{sceneTypeId}


    and s.name = #{name}


    and s.slice_status = #{sliceStatus}



        order by ps.seq, s.name


        order by s.seq, s.name

select count(*) from scene s

left join project_scene ps on s.id=ps.scene_id

where s.delete_flg=0

    and ps.project_id = #{projectId}


    and s.public_flg = #{publicFlg}


    and s.scene_type_id = #{sceneTypeId}


    and s.block_id = #{blockId}


    and s.src_block_id = #{srcBlockId}


    and s.slice_status = #{sliceStatus}


    and s.name = #{name}

mapper.update:
update scene set seq=case

    when id=#{sceneId} then #{index}

end
where id in

    #{sceneId}

mapper.select:
select distinct(s.id), s.name, s.scene_type_id as sceneTypeId,
               bst.name as sceneTypeName, bst.rel_scene_type_id as relSceneTypeId, rst.name as relSceneTypeName, s.code
from scene s
left join project_scene ps on ps.scene_id = s.id
left join project p on ps.project_id=p.id
left join block_scene_type bst on bst.id=s.scene_type_id
left join block_scene_type rst on bst.rel_scene_type_id=rst.id
where s.delete_flg=0

    and s.block_id = #{blockId}


    and s.src_block_id = #{srcBlockId}


    and ps.project_id = #{projectId}


    and s.public_flg = #{publicFlg}


    and s.scene_type_id = #{sceneTypeId}


    and s.slice_status = #{sliceStatus}


    and p.module_id = #{moduleId}


    and p.class_id = #{classId}



        order by ps.seq, s.name


        order by s.seq, s.name

select s.*,si.path as imageUrl, bst.name as sceneTypeName,
bst.name as sceneTypeName, bst.rel_scene_type_id as relSceneTypeId, rst.name as relSceneTypeName

    , ps.seq

from scene s left join block_image si on s.image_id=si.id
left join block_scene_type bst on bst.id=s.scene_type_id
left join block_scene_type rst on bst.rel_scene_type_id=rst.id

    left join project_scene ps on s.id=ps.scene_id

where s.delete_flg=0

    and ps.project_id = #{projectId}


    and s.code = #{sceneCode}


    and s.public_flg = #{publicFlg}


    and s.scene_type_id = #{sceneTypeId}


    and s.block_id = #{blockId}


    and s.src_block_id = #{srcBlockId}


    and s.name = #{name}


    and s.slice_status = #{sliceStatus}



        order by ps.seq


        order by s.seq
