mapper.update:
update task
set update_time = now(), update_by=#{updateBy}

    , name = #{name}


    , detail = #{detail}


    , type = #{type}


    , start_date = #{startDate}


    , expire_date = #{expireDate}


    , block_id = #{blockId}


    , project_id = #{projectId}


    , status = #{status}


    , update_by = #{updateBy}


    , version = #{version}


    , current_flg = #{currentFlg}

where id=#{id}

mapper.select:

select t.*, p.name as project_name,p.image_url as project_image_url, p.detail as project_detail, uc.sgname as creator_sgname, uu.sgname as updater_sgname
,p.ori_block_id as blockId, b.name as blockName, p.ori_enterprise_id as oriEnterpriseId, e.name as oriEnterpriseName, p.public_flg
from task t left join project p on t.project_id=p.id
left join block b on p.ori_block_id=b.id
left join enterprise e on p.ori_enterprise_id=e.id
left join user_profile uc on t.create_by = uc.user_id
left join user_profile uu on t.update_by = uu.user_id
where t.current_flg=1 and t.delete_flg=0

    and t.type = #{type}


    and t.status = #{status}


    and t.status in(#{item})


    and t.project_id = #{projectId}


    and t.user_id = #{userId}


    and t.expire_date >= #{startDate}


    and t.create_time <= #{endDate}

order by t.update_time desc, t.expire_date, t.id desc

select count(*)
from task t left join project p on t.project_id=p.id
left join user_profile u on p.creator_id = u.user_id
where t.current_flg=1 and t.delete_flg=0

    and t.type = #{type}


    and t.status = #{status}


    and t.status in(#{item})


    and t.project_id = #{projectId}


    and t.user_id = #{userId}


    and t.expire_date >= #{startDate}


    and t.create_time <= #{endDate}

select version, project_id as projectId, `type`, current_flg as currentFlg from task t
where t.delete_flg=0 and t.type = #{type} and t.project_id=#{projectId}
and t.version = (
  select max(version) from task a where a.delete_flg=0 and a.status>=2 and a.type = #{type} and a.project_id=#{projectId}
) limit 1

select id, name from task t
where 1=1

    and t.type = #{type}


    and t.status = #{status}


    and t.status in(#{item})


    and t.user_id = #{userId}

order by id desc

select t.*, p.name as project_name,p.image_url as project_image_url, p.detail as project_detail, uc.sgname as creator_sgname, uu.sgname as updater_sgname
from task t left join project p on t.project_id=p.id
left join user_profile uc on t.create_by = uc.user_id
left join user_profile uu on t.update_by = uu.user_id
where t.delete_flg=0

    and t.type = #{type}


    and t.status = #{status}


    and t.status in(#{item})


    and t.current_flg = #{currentFlg}


    and t.project_id = #{projectId}


    and t.user_id = #{userId}


    and t.expire_date >= #{startDate}


    and t.expire_date <= #{expireDateLt}


    and t.create_time <= #{endDate}

order by t.update_time desc, t.expire_date

mapper.update:
delete from task where project_id=#{projectId} and current_flg=1

    and type=#{type}


    and user_id=#{userId}
