mapper.insert:
update user_profile set user_id=#{userId}

    ,sgname = #{sgname}


    ,nickname = #{nickname}


    ,job = #{job}


    ,email = #{email}


    ,city = #{city}


    ,address = #{address}


    ,description = #{description}


    ,birthday = #{birthday}


    ,avatar = #{avatar}


    ,namecard = #{namecard}

where user_id=#{userId}

update user set id=#{id}

    ,user_type = #{userType}


    ,password = #{password}


    ,verify_code = #{verifyCode}


    ,ip = #{ip}


    ,last_login_time = #{lastLoginTime}

where id=#{id}

mapper.select:

select u.*, p.nickname,p.sgname,p.job,p.email,p.province,p.city,p.address, p.birthday, p.avatar, p.namecard from user u left join user_profile p on u.id=p.user_id
where u.delete_flg=0

    and u.verify_code = #{verifyCode}


    and u.password = #{password}


    and u.mobile = #{mobile}


    and p.sgname like concat('%',#{sgname},'%')


    and p.nickname like concat('%',#{nickname},'%')

order by u.id desc

select count(1) from `user` u left join user_profile p on u.id=p.user_id
where delete_flg=0

    and u.verify_code = #{verifyCode}


    and u.password = #{password}


    and u.mobile = #{mobile}


    and p.sgname like concat('%',#{sgname},'%')


    and p.nickname like concat('%',#{nickname},'%')
